sourcesContent:
'use strict';
const {
  Model
} = require('sequelize');
module.exports = (sequelize, DataTypes) => {
  class Complaint extends Model {
    /**
     * Helper method for defining associations.
     * This method is not a part of Sequelize lifecycle.
     * The `models/index` file will call this method automatically.
     */
    static associate(models) {
      // define association here
      Complaint.belongsTo(models.Category, {
        foreignKey:'categoryId',
        onDelete:'CASCADE',
        onUpdate:'CASCADE',
      });

      Complaint.belongsTo(models.Question, {
        foreignKey: "questionId",
        onDelete: "CASCADE",
        onUpdate: "CASCADE",
      });
       Complaint.belongsTo(models.User, {
        foreignKey: "userId",
        onDelete: "CASCADE",
        onUpdate: "CASCADE",
      });
      Complaint.hasMany(models.Action, {
        foreignKey:'complaintId',
        onDelete:'CASCADE',
        onUpdate:'CASCADE',
      });

      Complaint.belongsTo(models.Location, {
        foreignKey: "locationId",
        onDelete: "CASCADE",
        onUpdate: "CASCADE",
      });

    }

  }
  Complaint.init({
    date: DataTypes.STRING,
    time:DataTypes.STRING,
    additionalDetails:DataTypes.TEXT,
    categoryId: DataTypes.STRING,
    consent:DataTypes.BOOLEAN,
    locationId:DataTypes.STRING,
    userId: DataTypes.STRING,
    locationId: DataTypes.STRING,
    questionId: DataTypes.JSONB,
    description: DataTypes.TEXT,
    totalParcentage:DataTypes.DECIMAL,
    status: DataTypes.STRING
  }, {
    sequelize,
    modelName: 'Complaint',
  });
  return Complaint;
};

